mapper.select:
    	 		select 
						t1.id                             ,
						t1.rule_id                        ,
						t1.map_type                       ,
						t1.map_code                       
					
				from lbt_into_filter_rule_map 	t1
				where 1=1
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.RULE_ID =#{dto.ruleId}
    			
    			
						and t1.MAP_TYPE =#{dto.mapType}
    			
    			
						and t1.MAP_CODE =#{dto.mapCode}
    			
    			
						and t1.MAP_CODE in (${mapCodeStr})
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.rule_id                        ,
						t1.map_type                       ,
						t1.map_code                       
				from lbt_into_filter_rule_map 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.RULE_ID = #{dto.ruleId}
    			
    			
						and t1.MAP_TYPE = #{dto.mapType}
    			
    			
						and t1.MAP_CODE = #{dto.mapCode}
    			
    			
						and t1.MAP_CODE in (${mapCodeStr})

mapper.insert:

			select seq_lbt_into_filter_rule_map.nextval as id from dual

		insert into lbt_into_filter_rule_map
		(   		
				id                             ,
				rule_id                        ,
				map_type                       ,
				map_code                       
		)
		values(#{dto.id}
				,#{dto.ruleId,jdbcType=DECIMAL}
				,#{dto.mapType,jdbcType=VARCHAR}
				,#{dto.mapCode,jdbcType=VARCHAR}
		)

mapper.update:
     update lbt_into_filter_rule_map t1
           set 
		           t1.rule_id                        =#{dto.ruleId,jdbcType=DECIMAL},
		           t1.map_type                       =#{dto.mapType,jdbcType=VARCHAR},
		           t1.map_code                       =#{dto.mapCode,jdbcType=VARCHAR}
     where t1.id = #{dto.id}

mapper.insert:
begin
	
		insert into lbt_into_filter_rule_map
			(   		
					id                             ,
					rule_id                        ,
					map_type                       ,
					map_code                       
			)
			values(seq_lbt_into_filter_rule_map.nextval
					,#{ruleId}
					,#{mapType}
					,#{mapCode}
			);
	
end;
