mapper.select:
select t1.id , (select e.func_type from ext_function_type e where e.func_type_code = t1.func_type_code) as func_module_type, (select (select e2.func_type_name from ext_function_type e2 where e2.func_type_code = e1.func_type) from ext_function_type e1 where e1.func_type_code = t1.func_type_code) as func_module_name, t1.func_type_code , t2.func_type_name ,
t1.interface_no , t3.interface_name ,
t4.sys_name , t4.sys_code , t1.conf_type , t1.validate_state , t1.create_time , t1.create_by , t1.modify_time , t1.modify_by , t1.priority , t1.rule_group_code , (select s.ch_name from Sys_Rule_List s WHERE s.rule_code =t1.rule_group_code) rule_group_name from ext_function_interface_ref t1, ext_function_type t2,
ext_interface_info t3,
ext_sys_management t4 where t1.validate_state =1 and t2.validate_state =1
and t3.validate_state=1
and t4.validate_state=1 and t1.func_type_code = t2.func_type_code
and t1.interface_no = t3.interface_no
and t2.sys_code=t4.sys_code
and t1.ID =#{dto.id}
and t1.PRIORITY =#{dto.priority}
and t2.func_type_name like '%'||#{dto.funcTypeName}||'%'
and t4.sys_name like '%'||#{dto.sysName}||'%'
and t2.sys_code =#{dto.sysCode}
and t1.FUNC_TYPE_CODE =#{dto.funcTypeCode}
and exists ( select ex.id from ext_function_type ex where ex.func_type =#{dto.funcModuleType} and ex.func_type_code = t1.func_type_code )
and t1.INTERFACE_NO =#{dto.interfaceNo}
and t1.CONF_TYPE =#{dto.confType}
and t1.VALIDATE_STATE =#{dto.validateState}
and t1.CREATE_TIME =#{dto.createTime}
and t1.CREATE_BY =#{dto.createBy}
and t1.PRIORITY =#{dto.priority}
and t1.rule_group_code =#{dto.ruleGroupCode}
order by t1.id desc

select t1.id , t1.func_type_code , t2.func_type_name ,
t1.interface_no , t3.interface_name , t3.interface_send_type , t3.interface_params_type , t3.interface_url , t3.class_name ,
t4.sys_name , t4.sys_code , t1.conf_type , t1.validate_state , t1.create_time , t1.create_by , t1.modify_time , t1.modify_by , t1.priority , t1.rule_group_code from ext_function_interface_ref t1, ext_function_type t2,
ext_interface_info t3,
ext_sys_management t4 where t1.validate_state =1 and t2.validate_state =1
and t3.validate_state=1
and t4.validate_state=1 and t1.func_type_code = t2.func_type_code
and t1.interface_no = t3.interface_no
and t2.sys_code=t4.sys_code
and t1.ID =#{dto.id}
and t1.PRIORITY =#{dto.priority}
and t2.func_type_name like '%'||#{dto.funcTypeName}||'%'
and t4.sys_name like '%'||#{dto.sysName}||'%'
and t4.sys_code =#{dto.sysCode}
and t1.FUNC_TYPE_CODE =#{dto.funcTypeCode}
and t1.INTERFACE_NO =#{dto.interfaceNo}
and t1.CONF_TYPE =#{dto.confType}
and t1.VALIDATE_STATE =#{dto.validateState}
and t1.PRIORITY =#{dto.priority}
and t1.rule_group_code =#{dto.ruleGroupCode}
order by t1.PRIORITY

mapper.insert:

			select seq_EXT_FUNCTION_INTERFACE_REF.nextval as id from dual

		insert into ext_function_interface_ref
		(   		
				id                             ,
				func_type_code                 ,
				conf_type                      ,
				validate_state                 ,
				create_time                    ,
				create_by                                  
		)
		values(#{dto.id}
				,#{dto.funcTypeCode,jdbcType=VARCHAR}
				,#{dto.confType,jdbcType=VARCHAR}
				,'1'
				,systimestamp
				,#{dto.createBy,jdbcType=VARCHAR}
		)

    begin
	
		insert into ext_function_interface_ref
			(   		
					id                             ,
					func_type_code                 ,
					interface_no                   ,
					conf_type                      ,
					validate_state                 ,
					create_time                    ,
					create_by                      ,
					priority                       
			)
			values(seq_EXT_FUNCTION_INTERFACE_REF.nextval
					,#{funcTypeCode}
					,#{interfaceInfo.interfaceNo}
					,#{confType}
					,'1'
					,systimestamp
					,#{createBy}
					,#{interfaceInfo.priority}
			);
	
  end;
