mapper.select:
    	 		select 
						t1.id                             ,
						t1.interface_no                   ,
						t1.params_name                    ,
						t1.params_code                    ,
						t1.parent_params                  ,
						t1.validate_state                 ,
						t1.create_time                    ,
						t1.create_by                      ,
						t1.modify_time                    ,
			            t1.modify_by                      
					
				from ext_interface_params_ref 	t1
				where 1=1
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.INTERFACE_NO =#{dto.interfaceNo}
    			
    			
						and t1.PARAMS_NAME =#{dto.paramsName}
    			
    			
						and t1.PARAMS_CODE =#{dto.paramsCode}
    			
    			
						and t1.PARENT_PARAMS =#{dto.parentParams}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.interface_no                   ,
						t1.params_name                    ,
						t1.params_code                    ,
						t1.parent_params                  ,
						t1.validate_state                 ,
						t1.create_time                    ,
						t1.create_by                      ,
						t1.modify_time                    ,
			            t1.modify_by                      
				from ext_interface_params_ref 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.INTERFACE_NO = #{dto.interfaceNo}
    			
    			
						and t1.PARAMS_NAME = #{dto.paramsName}
    			
    			
						and t1.PARAMS_CODE = #{dto.paramsCode}
    			
    			
						and t1.PARENT_PARAMS = #{dto.parentParams}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}

mapper.insert:

			select seq_ext_interface_params_ref.nextval as id from dual

		insert into ext_interface_params_ref
		(   		
				id                             ,
				interface_no                   ,
				params_name                    ,
				params_code                    ,
				parent_params                  ,
				validate_state                 ,
				create_time                    ,
				create_by                                  
		)
		values(#{dto.id}
				,#{dto.interfaceNo,jdbcType=VARCHAR}
				,#{dto.paramsName,jdbcType=VARCHAR}
				,#{dto.paramsCode,jdbcType=VARCHAR}
				,#{dto.parentParams,jdbcType=VARCHAR}
				,'1'
				,systimestamp
				,#{dto.createBy,jdbcType=VARCHAR}
		)
