mapper.select:
    	     select 
						t1.id                             ,
						t1.rule_id                        ,
						t1.map_type                       ,
						t1.map_code                       ,
						t1.create_time                    ,
						t1.modify_time     
				from ext_invoke_filter_rule_map 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.RULE_ID = #{dto.ruleId}
    			
    			
						and t1.MAP_TYPE = #{dto.mapType}
    			
    			
						and t1.MAP_CODE = #{dto.mapCode}

mapper.insert:

			select seq_EXT_INVOKE_FILTER_RULE_MAP.nextval as id from dual

		insert into ext_invoke_filter_rule_map
		(   		
				id                             ,
				rule_id                        ,
				map_type                       ,
				map_code                       ,
				create_time 
		)
		values(#{dto.id}
				,#{dto.ruleId,jdbcType=DECIMAL}
				,#{dto.mapType,jdbcType=VARCHAR}
				,#{dto.mapCode,jdbcType=VARCHAR}
				,systimestamp
		)

begin
	
		insert into ext_invoke_filter_rule_map
			(   		
					id                             ,
					rule_id                        ,
					map_type                       ,
					map_code                       ,
					create_time 
			)
			values(seq_EXT_INVOKE_FILTER_RULE_MAP.nextval
					,#{ruleId}
					,#{mapType}
					,#{mapCode}
					,systimestamp
			);
	
end;

mapper.delete:
delete from ext_invoke_filter_rule_map t1 where 1=1
and t1.rule_id in (${ruleId})
and t1.map_type = #{mapType}
