mapper.select:
    	 		select 
						t1.id                             ,
						t1.provider_name                  ,
						t1.provider_code                  ,
						t1.validate_state                 ,
						t1.create_time                    ,
						t1.create_by                        ,
						t1.modify_time                    ,
						t1.modify_by                      
					
				from ext_provider_info 	t1
				where 1=1
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.PROVIDER_NAME like '%'||#{dto.providerName}||'%'
    			
    			
						and t1.PROVIDER_CODE =#{dto.providerCode}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.MODIFY_BY = #{dto.modifyBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.provider_name                  ,
						t1.provider_code                  ,
						t1.validate_state                 ,
						t1.create_time                    ,
						t1.create_by                        ,
						t1.modify_time                    ,
						t1.modify_by                      
				from ext_provider_info 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.PROVIDER_NAME like '%'||#{dto.providerName}||'%'
    			
    			
						and t1.PROVIDER_CODE = #{dto.providerCode}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.MODIFY_BY = #{dto.modifyBy}

mapper.insert:

			select seq_EXT_PROVIDER_INFO.nextval as id from dual

		insert into ext_provider_info
		(   		
				id                             ,
				provider_name                  ,
				provider_code                  ,
				validate_state                 ,
				create_time                    ,
				create_by                        ,
				modify_time                    ,
				modify_by                      
		)
		values(#{dto.id}
				,#{dto.providerName,jdbcType=VARCHAR}
				,#{dto.providerCode,jdbcType=VARCHAR}
				,'1'
				,systimestamp
				,#{dto.createBy,jdbcType=VARCHAR}
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,#{dto.modifyBy,jdbcType=VARCHAR}
		)
