mapper.select:
select t1.id , t1.provider_code , t1.interface_no , t2.interface_name , t1.validate_state , t1.create_time , t1.create_by , t1.modify_time , t1.modify_by from ext_provider_interface_ref t1,ext_interface_info t2 where 1=1 and t1.interface_no=t2.interface_no and t1.validate_state='1' and t2.validate_state='1'
and t1.ID =#{dto.id}
and t1.PROVIDER_CODE =#{dto.providerCode}
and t1.INTERFACE_NO =#{dto.interfaceNo}
and t1.VALIDATE_STATE =#{dto.validateState}
and t1.CREATE_TIME =#{dto.createTime}
and t1.CREATE_BY =#{dto.createBy}
order by t1.id desc

    	     select 
						t1.id                             ,
						t1.provider_code                  ,
						t1.interface_no                   ,
						t1.validate_state                 ,
						t1.create_time                    ,
						t1.create_by                      ,
						t1.modify_time                    ,
						t1.modify_by 
				from ext_provider_interface_ref 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.PROVIDER_CODE = #{dto.providerCode}
    			
    			
						and t1.INTERFACE_NO = #{dto.interfaceNo}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}

select t1.id , t1.provider_code , t1.interface_no , t2.interface_name , t1.validate_state , t1.create_time , t1.create_by , t1.modify_time , t1.modify_by from ext_provider_interface_ref t1,ext_interface_info t2 where 1=1 and t1.interface_no=t2.interface_no and t1.validate_state='1' and t2.validate_state='1'
and t1.ID = #{dto.id}
and t1.PROVIDER_CODE = #{dto.providerCode}
and t1.INTERFACE_NO = #{dto.interfaceNo}
and t1.VALIDATE_STATE = #{dto.validateState}
and t1.CREATE_TIME = #{dto.createTime}
and t1.CREATE_BY = #{dto.createBy}

mapper.insert:

			select seq_EXT_PROVIDER_INTERFACE_REF.nextval as id from dual

		insert into ext_provider_interface_ref
		(   		
				id                             ,
				provider_code                  ,
				interface_no                   ,
				validate_state                 ,
				create_time                    ,
				create_by                      
		)
		values(#{dto.id}
				,#{dto.providerCode,jdbcType=VARCHAR}
				,#{dto.interfaceNo,jdbcType=VARCHAR}
				,'1'
				,systimestamp
				,#{dto.createBy,jdbcType=VARCHAR}
		)

begin
	
		insert into ext_provider_interface_ref
			(   		
					id             ,
					provider_code  ,
					interface_no   ,
					validate_state ,
					create_time    ,
					create_by                 
			)
			values(seq_EXT_PROVIDER_INTERFACE_REF.nextval
					,#{providerCode}
					,#{interfaceNo}
					,'1'
					,systimestamp
					,#{createBy}
			);
	
end;
