mapper.select:
select t1.id , t1.sys_code , t1.sys_name , t1.validate_state , t1.create_by , t1.create_time , t1.modify_by , t1.modify_time from ext_sys_management t1 where 1=1
and t1.ID =#{dto.id}
and t1.SYS_CODE =#{dto.sysCode}
and t1.SYS_NAME like '%'||#{dto.sysName}||'%'
and t1.VALIDATE_STATE =#{dto.validateState}
order by t1.id desc

select t1.id , t1.sys_code , t1.sys_name , t1.validate_state , t1.create_by , t1.create_time , t1.modify_by , t1.modify_time from ext_sys_management t1 where 1=1
and t1.ID = #{dto.id}
and t1.SYS_CODE = #{dto.sysCode}
and t1.SYS_NAME like '%'||#{dto.sysName}||'%'
and t1.VALIDATE_STATE = #{dto.validateState}

mapper.insert:

			select seq_EXT_SYS_MANAGEMENT.nextval as id from dual

		insert into ext_sys_management
		(   		
				id                             ,
				sys_code                       ,
				sys_name                       ,
				validate_state                 ,	
				create_by                      ,
				create_time                    
				
		)
		values(#{dto.id}
				,#{dto.sysCode,jdbcType=VARCHAR}
				,#{dto.sysName,jdbcType=VARCHAR}
				,'1'
				,#{dto.createBy,jdbcType=DECIMAL}
				,systimestamp
		)
