mapper.select:
select t1.id , t1.rule_type , t1.ch_name , t1.en_name , t1.eftect_time , t1.validate_state , t1.rule_code , t1.remark , t1.create_user , t1.create_date , t1.update_user , t1.update_date , t1.new_version_is_update , t1.expires_time , t1.rule_resource , t1.resource_type , t1.version from sys_rule_list t1 where t1.validate_state = 1
and t1.en_name =#{dto.enName}
and t1.rule_type =#{dto.ruleType}

mapper.insert:

			select seq_SYS_RULE_LIST.nextval as id from dual

		insert into sys_rule_list
		(   		
				id                             ,
				rule_type                      ,
				ch_name                        ,
				en_name                        ,
				eftect_time                    ,
				validate_state                 ,
				rule_code                      ,
				remark                         ,
				create_user                    ,
				create_date                    ,
				new_version_is_update          ,
				expires_time                   ,
				rule_resource                  ,
				resource_type                  ,
				version                   
		)
		values(#{dto.id}
				,#{dto.ruleType,jdbcType=VARCHAR}
				,#{dto.chName,jdbcType=VARCHAR}
				,#{dto.enName,jdbcType=VARCHAR}
				,#{dto.eftectTime,jdbcType=DATE}
				,'1'
				,#{dto.ruleCode,jdbcType=VARCHAR}
				,#{dto.remark,jdbcType=VARCHAR}
				,#{dto.createUser,jdbcType=VARCHAR}
				,sysdate
				,#{0,jdbcType=DECIMAL}
				,#{dto.expiresTime,jdbcType=DATE}
				,#{dto.ruleResource,jdbcType=VARCHAR}
				,#{dto.resourceType,jdbcType=VARCHAR}
				,'0'
		)

mapper.update:
update sys_rule_list t1
      set   t1.new_version_is_update='1'	
where t1.rule_code = ${code}

mapper.select:
select s1.ch_name rule_base_name, s1.rule_code rule_base_code, s2.ch_name rule_group_name, s2.rule_code rule_group_code, s3.ch_name rule_name, s3.rule_code, s3.remark, s3.validate_state, s2.eftect_time, s2.expires_time, s3.id from sys_rule_list s3 left join sys_rule_list s2 on s2.rule_code=substr(s3.rule_code,0,6) and s3.rule_type='4' and s2.rule_type='2' left join sys_rule_list s1 on s1.rule_code=substr(s2.rule_code,0,3) and s2.rule_type='2' and s1.rule_type='1' where s3.rule_type='4'
and s3.validate_state=#{dto.validateState}
and s1.rule_code =#{dto.ruleBaseCode}
and s2.rule_code =#{dto.ruleGroupCode}
and s3.rule_code =#{dto.ruleCode}
