mapper.select:
    	 		select 
						t1.id                             ,
						t1.excution_code                  ,
						t1.rule_code                      ,
						t1.remark                         ,
						t1.create_user                    ,
						t1.create_date                    ,
						t1.update_user                    ,
						t1.update_date                    ,
						t1.version_num                    ,
						t1.new_version_is_update          ,
						t1.help                           ,
						t1.validate_state                 ,
						t1.parent_code                    
					
				from sys_rule_then_config 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.EXCUTION_CODE =#{dto.excutionCode}
    			
    			
						and t1.RULE_CODE =#{dto.ruleCode}
    			
    			
						and t1.REMARK =#{dto.remark}
    			
    			
						and t1.CREATE_USER =#{dto.createUser}
    			
    			
						and t1.CREATE_DATE =#{dto.createDate}
    			
    			
						and t1.UPDATE_USER =#{dto.updateUser}
    			
    			
						and t1.UPDATE_DATE =#{dto.updateDate}
    			
    			
						and t1.VERSION_NUM =#{dto.versionNum}
    			
    			
						and t1.NEW_VERSION_IS_UPDATE =#{dto.newVersionIsUpdate}
    			
    			
						and t1.HELP =#{dto.help}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.PARENT_CODE =#{dto.parentCode}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.excution_code                  ,
						t1.rule_code                      ,
						t1.remark                         ,
						t1.create_user                    ,
						t1.create_date                    ,
						t1.update_user                    ,
						t1.update_date                    ,
						t1.version_num                    ,
						t1.new_version_is_update          ,
						t1.help                           ,
						t1.validate_state                 ,
						t1.parent_code                    
				from sys_rule_then_config 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.EXCUTION_CODE = #{dto.excutionCode}
    			
    			
						and t1.RULE_CODE = #{dto.ruleCode}
    			
    			
						and t1.REMARK = #{dto.remark}
    			
    			
						and t1.CREATE_USER = #{dto.createUser}
    			
    			
						and t1.CREATE_DATE = #{dto.createDate}
    			
    			
						and t1.UPDATE_USER = #{dto.updateUser}
    			
    			
						and t1.UPDATE_DATE = #{dto.updateDate}
    			
    			
						and t1.VERSION_NUM = #{dto.versionNum}
    			
    			
						and t1.NEW_VERSION_IS_UPDATE = #{dto.newVersionIsUpdate}
    			
    			
						and t1.HELP = #{dto.help}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.PARENT_CODE = #{dto.parentCode}

mapper.insert:

			select seq_SYS_RULE_THEN_CONFIG.nextval as id from dual

		insert into sys_rule_then_config
		(   		
				id                             ,
				excution_code                  ,
				rule_code                      ,
				remark                         ,
				version_num                    ,
				help                           ,
				create_user                    ,
				create_date                    
		)
		values(#{dto.id}
				,#{dto.excutionCode,jdbcType=VARCHAR}
				,#{dto.ruleCode,jdbcType=VARCHAR}
				,#{dto.remark,jdbcType=VARCHAR}
				,'1'
				,#{dto.help,jdbcType=VARCHAR}
				,#{dto.createUser,jdbcType=VARCHAR}
				,sysdate
		)
