mapper.select:
select t1.keyid , t1.datatime , t1.allmsg , t1.objection , t1.objectiontime , t1.create_time , t1.modify_time , t1.fk_id
from ext_law_person_tax t1 where 1=1 and t1.validate_state='1'
and t1.KEYID =#{dto.keyid}
and t1.ALLMSG =#{dto.allmsg}
and t1.OBJECTION =#{dto.objection}
and t1.OBJECTIONTIME =#{dto.objectiontime}
and t1.FK_ID =#{dto.fkId}
order by t1.id desc

    	     select 
						t1.keyid                          ,
						t1.datatime                       ,
						t1.allmsg                         ,
						t1.objection                      ,
						t1.objectiontime                  ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.fk_id                          
				from ext_law_person_tax 		t1
				where 1=1
    			
						and t1.KEYID = #{dto.keyid}
    			
    			
						and t1.ALLMSG = #{dto.allmsg}
    			
    			
						and t1.OBJECTION = #{dto.objection}
    			
    			
						and t1.OBJECTIONTIME = #{dto.objectiontime}
    			
    			
						and t1.FK_ID = #{dto.fkId}

mapper.insert:
insert into ext_law_person_tax
(   		
		keyid                          ,
		datatime                       ,
		allmsg                         ,
		objection                      ,
		objectiontime                  ,
		create_time                    ,
		fk_id                          
)
values(#{dto.keyid}
		,#{dto.datatime,jdbcType=VARCHAR}
		,#{dto.allmsg,jdbcType=VARCHAR}
		,#{dto.objection,jdbcType=VARCHAR}
		,#{dto.objectiontime,jdbcType=VARCHAR}
		,systimestamp
		,#{dto.fkId,jdbcType=DECIMAL}
)
