mapper.select:
    	 		select 
						t1.id                             ,
						t1.user_grid_id                   ,
						t1.susp_name                      ,
						t1.susp_updt                      ,
						t1.create_by                      ,
						t1.create_time                    ,
						t1.modify_by                      ,
						t1.modify_time                    ,
						t1.validate_state                 
					
				from ext_wl_jxl_mg_idcardnames 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.USER_GRID_ID =#{dto.userGridId}
    			
    			
						and t1.SUSP_NAME =#{dto.suspName}
    			
    			
						and t1.SUSP_UPDT =#{dto.suspUpdt}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.MODIFY_BY =#{dto.modifyBy}
    			
    			
						and t1.MODIFY_TIME =#{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.user_grid_id                   ,
						t1.susp_name                      ,
						t1.susp_updt                      ,
						t1.create_by                      ,
						t1.create_time                    ,
						t1.modify_by                      ,
						t1.modify_time                    ,
						t1.validate_state                 
				from ext_wl_jxl_mg_idcardnames 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.USER_GRID_ID = #{dto.userGridId}
    			
    			
						and t1.SUSP_NAME = #{dto.suspName}
    			
    			
						and t1.SUSP_UPDT = #{dto.suspUpdt}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.MODIFY_BY = #{dto.modifyBy}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}

mapper.insert:

			select seq_EXT_WL_JXL_MG_IDCARDNAMES.nextval as id from dual

		insert into ext_wl_jxl_mg_idcardnames
		(   		
				id                             ,
				user_grid_id                   ,
				susp_name                      ,
				susp_updt                      ,
				create_by                      ,
				create_time                    ,
				modify_by                      ,
				modify_time                    ,
				validate_state                 
		)
		values(#{dto.id}
				,#{dto.userGridId,jdbcType=VARCHAR}
				,#{dto.suspName,jdbcType=VARCHAR}
				,#{dto.suspUpdt,jdbcType=VARCHAR}
				,#{dto.createBy,jdbcType=DECIMAL}
				,systimestamp
				,#{dto.modifyBy,jdbcType=DECIMAL}
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,'1'
		)
