mapper.select:
    	 		select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.asset_disposal                 ,
						t1.compensate                     ,
						t1.intro                          ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
					
				from ext_wl_sh_credit_record_intro 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.FK_REPORT_ID =#{dto.fkReportId}
    			
    			
						and t1.ASSET_DISPOSAL =#{dto.assetDisposal}
    			
    			
						and t1.COMPENSATE =#{dto.compensate}
    			
    			
						and t1.INTRO =#{dto.intro}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.MODIFY_TIME =#{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
    			
						and t1.UPDATE_BY =#{dto.updateBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.asset_disposal                 ,
						t1.compensate                     ,
						t1.intro                          ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
				from ext_wl_sh_credit_record_intro 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.FK_REPORT_ID = #{dto.fkReportId}
    			
    			
						and t1.ASSET_DISPOSAL = #{dto.assetDisposal}
    			
    			
						and t1.COMPENSATE = #{dto.compensate}
    			
    			
						and t1.INTRO = #{dto.intro}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.UPDATE_BY = #{dto.updateBy}

mapper.insert:

			select seq_EXT_WL_SH_CREDIT_RECORD_IN.nextval as id from dual

		insert into ext_wl_sh_credit_record_intro
		(   		
				id                             ,
				fk_report_id                   ,
				asset_disposal                 ,
				compensate                     ,
				intro                          ,
				create_time                    ,
				modify_time                    ,
				validate_state                 ,
				create_by                      ,
				update_by                      
		)
		values(#{dto.id}
				,#{dto.fkReportId,jdbcType=DECIMAL}
				,#{dto.assetDisposal,jdbcType=VARCHAR}
				,#{dto.compensate,jdbcType=VARCHAR}
				,#{dto.intro,jdbcType=VARCHAR}
				,systimestamp
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,'1'
				,#{dto.createBy,jdbcType=DECIMAL}
				,#{dto.updateBy,jdbcType=DECIMAL}
		)
