mapper.select:
    	 		select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.original                       ,
						t1.organization                   ,
						t1.docket_no                      ,
						t1.reconsideration_flag           ,
						t1.reconsideration                ,
						t1.punishment_content             ,
						t1.punishment_amount              ,
						t1.punishment_effective           ,
						t1.punishment_deadline            ,
						t1.isfail                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
					
				from ext_wl_sh_sr_punishments 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.FK_REPORT_ID =#{dto.fkReportId}
    			
    			
						and t1.ORIGINAL =#{dto.original}
    			
    			
						and t1.ORGANIZATION =#{dto.organization}
    			
    			
						and t1.DOCKET_NO =#{dto.docketNo}
    			
    			
						and t1.RECONSIDERATION_FLAG =#{dto.reconsiderationFlag}
    			
    			
						and t1.RECONSIDERATION =#{dto.reconsideration}
    			
    			
						and t1.PUNISHMENT_CONTENT =#{dto.punishmentContent}
    			
    			
						and t1.PUNISHMENT_AMOUNT =#{dto.punishmentAmount}
    			
    			
						and t1.PUNISHMENT_EFFECTIVE =#{dto.punishmentEffective}
    			
    			
						and t1.PUNISHMENT_DEADLINE =#{dto.punishmentDeadline}
    			
    			
						and t1.ISFAIL =#{dto.isfail}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.MODIFY_TIME =#{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
    			
						and t1.UPDATE_BY =#{dto.updateBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.original                       ,
						t1.organization                   ,
						t1.docket_no                      ,
						t1.reconsideration_flag           ,
						t1.reconsideration                ,
						t1.punishment_content             ,
						t1.punishment_amount              ,
						t1.punishment_effective           ,
						t1.punishment_deadline            ,
						t1.isfail                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
				from ext_wl_sh_sr_punishments 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.FK_REPORT_ID = #{dto.fkReportId}
    			
    			
						and t1.ORIGINAL = #{dto.original}
    			
    			
						and t1.ORGANIZATION = #{dto.organization}
    			
    			
						and t1.DOCKET_NO = #{dto.docketNo}
    			
    			
						and t1.RECONSIDERATION_FLAG = #{dto.reconsiderationFlag}
    			
    			
						and t1.RECONSIDERATION = #{dto.reconsideration}
    			
    			
						and t1.PUNISHMENT_CONTENT = #{dto.punishmentContent}
    			
    			
						and t1.PUNISHMENT_AMOUNT = #{dto.punishmentAmount}
    			
    			
						and t1.PUNISHMENT_EFFECTIVE = #{dto.punishmentEffective}
    			
    			
						and t1.PUNISHMENT_DEADLINE = #{dto.punishmentDeadline}
    			
    			
						and t1.ISFAIL = #{dto.isfail}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.UPDATE_BY = #{dto.updateBy}

mapper.insert:

			select seq_EXT_WL_SH_SR_PUNISHMENTS.nextval as id from dual

		insert into ext_wl_sh_sr_punishments
		(   		
				id                             ,
				fk_report_id                   ,
				original                       ,
				organization                   ,
				docket_no                      ,
				reconsideration_flag           ,
				reconsideration                ,
				punishment_content             ,
				punishment_amount              ,
				punishment_effective           ,
				punishment_deadline            ,
				isfail                         ,
				create_time                    ,
				modify_time                    ,
				validate_state                 ,
				create_by                      ,
				update_by                      
		)
		values(#{dto.id}
				,#{dto.fkReportId,jdbcType=DECIMAL}
				,#{dto.original,jdbcType=VARCHAR}
				,#{dto.organization,jdbcType=VARCHAR}
				,#{dto.docketNo,jdbcType=VARCHAR}
				,#{dto.reconsiderationFlag,jdbcType=VARCHAR}
				,#{dto.reconsideration,jdbcType=VARCHAR}
				,#{dto.punishmentContent,jdbcType=VARCHAR}
				,#{dto.punishmentAmount,jdbcType=VARCHAR}
				,#{dto.punishmentEffective,jdbcType=VARCHAR}
				,#{dto.punishmentDeadline,jdbcType=VARCHAR}
				,#{dto.isfail,jdbcType=VARCHAR}
				,systimestamp
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,'1'
				,#{dto.createBy,jdbcType=DECIMAL}
				,#{dto.updateBy,jdbcType=DECIMAL}
		)

begin

	select seq_EXT_WL_SH_SR_PUNISHMENTS.nextval as id from dual


	insert into ext_wl_sh_sr_punishments
	(
	id,
	fk_report_id,
	original,
	organization,
	docket_no,
	reconsideration_flag,
	reconsideration,
	punishment_content,
	punishment_amount,
	punishment_effective,
	punishment_deadline,
	isfail,
	create_time,
	validate_state
	)
	values(seq_EXT_WL_SH_SR_PUNISHMENTS.nextval
	,#{fkReportId}
	,#{dto.original,jdbcType=VARCHAR}
	,#{dto.organization,jdbcType=VARCHAR}
	,#{dto.docketNo,jdbcType=VARCHAR}
	,#{dto.reconsiderationFlag,jdbcType=VARCHAR}
	,#{dto.reconsideration,jdbcType=VARCHAR}
	,#{dto.punishmentContent,jdbcType=VARCHAR}
	,#{dto.punishmentAmount,jdbcType=VARCHAR}
	,#{dto.punishmentEffective,jdbcType=VARCHAR}
	,#{dto.punishmentDeadline,jdbcType=VARCHAR}
