mapper.select:
    	 		select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.original                       ,
						t1.government                     ,
						t1.record_time                    ,
						t1.amount                         ,
						t1.id_no                          ,
						t1.isfail                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
					
				from ext_wl_sh_sr_taxs 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.FK_REPORT_ID =#{dto.fkReportId}
    			
    			
						and t1.ORIGINAL =#{dto.original}
    			
    			
						and t1.GOVERNMENT =#{dto.government}
    			
    			
						and t1.RECORD_TIME =#{dto.recordTime}
    			
    			
						and t1.AMOUNT =#{dto.amount}
    			
    			
						and t1.ID_NO =#{dto.idNo}
    			
    			
						and t1.ISFAIL =#{dto.isfail}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.MODIFY_TIME =#{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE =#{dto.validateState}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
    			
						and t1.UPDATE_BY =#{dto.updateBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.original                       ,
						t1.government                     ,
						t1.record_time                    ,
						t1.amount                         ,
						t1.id_no                          ,
						t1.isfail                         ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.validate_state                 ,
						t1.create_by                      ,
						t1.update_by                      
				from ext_wl_sh_sr_taxs 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.FK_REPORT_ID = #{dto.fkReportId}
    			
    			
						and t1.ORIGINAL = #{dto.original}
    			
    			
						and t1.GOVERNMENT = #{dto.government}
    			
    			
						and t1.RECORD_TIME = #{dto.recordTime}
    			
    			
						and t1.AMOUNT = #{dto.amount}
    			
    			
						and t1.ID_NO = #{dto.idNo}
    			
    			
						and t1.ISFAIL = #{dto.isfail}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.VALIDATE_STATE = #{dto.validateState}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.UPDATE_BY = #{dto.updateBy}

mapper.insert:

			select seq_EXT_WL_SH_SR_TAXS.nextval as id from dual

		insert into ext_wl_sh_sr_taxs
		(   		
				id                             ,
				fk_report_id                   ,
				original                       ,
				government                     ,
				record_time                    ,
				amount                         ,
				id_no                          ,
				isfail                         ,
				create_time                    ,
				modify_time                    ,
				validate_state                 ,
				create_by                      ,
				update_by                      
		)
		values(#{dto.id}
				,#{dto.fkReportId,jdbcType=DECIMAL}
				,#{dto.original,jdbcType=VARCHAR}
				,#{dto.government,jdbcType=VARCHAR}
				,#{dto.recordTime,jdbcType=VARCHAR}
				,#{dto.amount,jdbcType=VARCHAR}
				,#{dto.idNo,jdbcType=VARCHAR}
				,#{dto.isfail,jdbcType=VARCHAR}
				,systimestamp
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,'1'
				,#{dto.createBy,jdbcType=DECIMAL}
				,#{dto.updateBy,jdbcType=DECIMAL}
		)

begin

	select seq_EXT_WL_SH_SR_TAXS.nextval as id from dual


	insert into ext_wl_sh_sr_taxs
	(
	id,
	fk_report_id,
	original,
	government,
	record_time,
	amount,
	id_no,
	isfail,
	create_time,
	validate_state
	)
	values(seq_EXT_WL_SH_SR_TAXS.nextval
	,#{fkReportId}
	,#{dto.original,jdbcType=VARCHAR}
	,#{dto.government,jdbcType=VARCHAR}
	,#{dto.recordTime,jdbcType=VARCHAR}
	,#{dto.amount,jdbcType=VARCHAR}
	,#{dto.idNo,jdbcType=VARCHAR}
	,#{dto.isfail,jdbcType=VARCHAR}
	,systimestamp
	,'1'
	);

end;
