mapper.select:
    	 		select 
						t1.id                             ,
						t1.into_cust_ref_id               ,
						t1.code                           ,
						t1.ret_desc                       ,
						t1.trans_id                       ,
						t1.trade_no                       ,
						t1.fee                            ,
						t1.id_no                          ,
						t1.id_name                        ,
						t1.member_count                   ,
						t1.order_count                    ,
						t1.debt_amount                    ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.create_by                      ,
						t1.modify_by                      
					
				from ext_wl_xy_overdue 	t1
				where 1=1
				and t1.validate_state='1'
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.INTO_CUST_REF_ID =#{dto.intoCustRefId}
    			
    			
						and t1.CODE =#{dto.code}
    			
    			
						and t1.RET_DESC =#{dto.retDesc}
    			
    			
						and t1.TRANS_ID =#{dto.transId}
    			
    			
						and t1.TRADE_NO =#{dto.tradeNo}
    			
    			
						and t1.FEE =#{dto.fee}
    			
    			
						and t1.ID_NO =#{dto.idNo}
    			
    			
						and t1.ID_NAME =#{dto.idName}
    			
    			
						and t1.MEMBER_COUNT =#{dto.memberCount}
    			
    			
						and t1.ORDER_COUNT =#{dto.orderCount}
    			
    			
						and t1.DEBT_AMOUNT =#{dto.debtAmount}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
    			
						and t1.MODIFY_TIME =#{dto.modifyTime}
    			
    			
						and t1.CREATE_BY =#{dto.createBy}
    			
    			
						and t1.MODIFY_BY =#{dto.modifyBy}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.into_cust_ref_id               ,
						t1.code                           ,
						t1.ret_desc                       ,
						t1.trans_id                       ,
						t1.trade_no                       ,
						t1.fee                            ,
						t1.id_no                          ,
						t1.id_name                        ,
						t1.member_count                   ,
						t1.order_count                    ,
						t1.debt_amount                    ,
						t1.create_time                    ,
						t1.modify_time                    ,
						t1.create_by                      ,
						t1.modify_by                      
				from ext_wl_xy_overdue 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.INTO_CUST_REF_ID = #{dto.intoCustRefId}
    			
    			
						and t1.CODE = #{dto.code}
    			
    			
						and t1.RET_DESC = #{dto.retDesc}
    			
    			
						and t1.TRANS_ID = #{dto.transId}
    			
    			
						and t1.TRADE_NO = #{dto.tradeNo}
    			
    			
						and t1.FEE = #{dto.fee}
    			
    			
						and t1.ID_NO = #{dto.idNo}
    			
    			
						and t1.ID_NAME = #{dto.idName}
    			
    			
						and t1.MEMBER_COUNT = #{dto.memberCount}
    			
    			
						and t1.ORDER_COUNT = #{dto.orderCount}
    			
    			
						and t1.DEBT_AMOUNT = #{dto.debtAmount}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}
    			
    			
						and t1.MODIFY_TIME = #{dto.modifyTime}
    			
    			
						and t1.CREATE_BY = #{dto.createBy}
    			
    			
						and t1.MODIFY_BY = #{dto.modifyBy}

mapper.insert:

			select seq_EXT_WL_XY_OVERDUE.nextval as id from dual

		insert into ext_wl_xy_overdue
		(   		
				id                             ,
				into_cust_ref_id               ,
				code                           ,
				ret_desc                       ,
				trans_id                       ,
				trade_no                       ,
				fee                            ,
				id_no                          ,
				id_name                        ,
				member_count                   ,
				order_count                    ,
				debt_amount                    ,
				create_time                    ,
				modify_time                    ,
				create_by                      ,
				modify_by                      
		)
		values(#{dto.id}
				,#{dto.intoCustRefId,jdbcType=DECIMAL}
				,#{dto.code,jdbcType=VARCHAR}
				,#{dto.retDesc,jdbcType=VARCHAR}
				,#{dto.transId,jdbcType=VARCHAR}
				,#{dto.tradeNo,jdbcType=VARCHAR}
				,#{dto.fee,jdbcType=VARCHAR}
				,#{dto.idNo,jdbcType=VARCHAR}
				,#{dto.idName,jdbcType=VARCHAR}
				,#{dto.memberCount,jdbcType=VARCHAR}
				,#{dto.orderCount,jdbcType=VARCHAR}
				,#{dto.debtAmount,jdbcType=VARCHAR}
				,systimestamp
				,#{dto.modifyTime,jdbcType=TIMESTAMP}
				,#{dto.createBy,jdbcType=DECIMAL}
				,#{dto.modifyBy,jdbcType=DECIMAL}
		)
