mapper.select:
    	 		select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.ryname                         ,
						t1.entname                        ,
						t1.regno                          ,
						t1.enttype                        ,
						t1.regcap                         ,
						t1.regcapcur                      ,
						t1.entstatus                      ,
						t1.subconam                       ,
						t1.currency                       ,
						t1.conform                        ,
						t1.fundedratio                    ,
						t1.create_time                    
					
				from ext_yszx_shareholder_info 	t1
				where 1=1
    			
						and t1.ID =#{dto.id}
    			
    			
						and t1.FK_REPORT_ID =#{dto.fkReportId}
    			
    			
						and t1.RYNAME =#{dto.ryname}
    			
    			
						and t1.ENTNAME =#{dto.entname}
    			
    			
						and t1.REGNO =#{dto.regno}
    			
    			
						and t1.ENTTYPE =#{dto.enttype}
    			
    			
						and t1.REGCAP =#{dto.regcap}
    			
    			
						and t1.REGCAPCUR =#{dto.regcapcur}
    			
    			
						and t1.ENTSTATUS =#{dto.entstatus}
    			
    			
						and t1.SUBCONAM =#{dto.subconam}
    			
    			
						and t1.CURRENCY =#{dto.currency}
    			
    			
						and t1.CONFORM =#{dto.conform}
    			
    			
						and t1.FUNDEDRATIO =#{dto.fundedratio}
    			
    			
						and t1.CREATE_TIME =#{dto.createTime}
    			
				
				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.fk_report_id                   ,
						t1.ryname                         ,
						t1.entname                        ,
						t1.regno                          ,
						t1.enttype                        ,
						t1.regcap                         ,
						t1.regcapcur                      ,
						t1.entstatus                      ,
						t1.subconam                       ,
						t1.currency                       ,
						t1.conform                        ,
						t1.fundedratio                    ,
						t1.create_time                    
				from ext_yszx_shareholder_info 		t1
				where 1=1
    			
						and t1.ID = #{dto.id}
    			
    			
						and t1.FK_REPORT_ID = #{dto.fkReportId}
    			
    			
						and t1.RYNAME = #{dto.ryname}
    			
    			
						and t1.ENTNAME = #{dto.entname}
    			
    			
						and t1.REGNO = #{dto.regno}
    			
    			
						and t1.ENTTYPE = #{dto.enttype}
    			
    			
						and t1.REGCAP = #{dto.regcap}
    			
    			
						and t1.REGCAPCUR = #{dto.regcapcur}
    			
    			
						and t1.ENTSTATUS = #{dto.entstatus}
    			
    			
						and t1.SUBCONAM = #{dto.subconam}
    			
    			
						and t1.CURRENCY = #{dto.currency}
    			
    			
						and t1.CONFORM = #{dto.conform}
    			
    			
						and t1.FUNDEDRATIO = #{dto.fundedratio}
    			
    			
						and t1.CREATE_TIME = #{dto.createTime}

mapper.insert:

			select seq_ext_yszx_shareholder_info.nextval as id from dual

		insert into ext_yszx_shareholder_info
		(   		
				id                             ,
				fk_report_id                   ,
				ryname                         ,
				entname                        ,
				regno                          ,
				enttype                        ,
				regcap                         ,
				regcapcur                      ,
				entstatus                      ,
				subconam                       ,
				currency                       ,
				conform                        ,
				fundedratio                    ,
				create_time                    
		)
		values(#{dto.id}
				,#{dto.fkReportId,jdbcType=DECIMAL}
				,#{dto.ryname,jdbcType=VARCHAR}
				,#{dto.entname,jdbcType=VARCHAR}
				,#{dto.regno,jdbcType=VARCHAR}
				,#{dto.enttype,jdbcType=VARCHAR}
				,#{dto.regcap,jdbcType=VARCHAR}
				,#{dto.regcapcur,jdbcType=VARCHAR}
				,#{dto.entstatus,jdbcType=VARCHAR}
				,#{dto.subconam,jdbcType=VARCHAR}
				,#{dto.currency,jdbcType=VARCHAR}
				,#{dto.conform,jdbcType=VARCHAR}
				,#{dto.fundedratio,jdbcType=VARCHAR}
				,systimestamp
		)
